mutation addItemToCart {
    addItemToCart(input: {
        productId: 1
        quantity: 1

        # Only use while adding configurable product to cart
        selectedConfigurableOption: 4
        superAttribute: [{
            attributeId: 23
            attributeOptionId: 4
        },  {
            attributeId: 24
            attributeOptionId: 6
        }]

        # Only use while adding grouped product to cart
        qty: [{
            productId: 5
            quantity: 3
        },  {
            productId: 1
            quantity: 2
        }]

        # Only use while adding downloadable product to cart
        links: [6, 8]

        # Only use while adding bundled product to cart
        bundleOptions: [{
            bundleOptionId: 9
            bundleOptionProductId: [8]
            qty: 2
        },  {
            bundleOptionId: 10
            bundleOptionProductId: [10]
            qty: 3
        },  {
            bundleOptionId: 11
            bundleOptionProductId: [11, 12]
        },  {
            bundleOptionId: 13
            bundleOptionProductId: [15]
        }]

        # Only use while adding booking(default, appointment, rental, table) product to cart
        booking: {
            date: "2021-02-15"
            slot: {
                from: 1613374200
                to: 1613376900
            }
            note: "Booking Table"

            # Only use while adding booking(event) product to cart
            qty: [{
                ticketId: 11
                quantity: 2
            },  {
                ticketId: 12
                quantity: 1
            }]
        }
    }) {
        success
        cart {
            id
            customerEmail
            customerFirstName
            customerLastName
            shippingMethod
            couponCode
            isGift
            itemsCount
            itemsQty
            exchangeRate
            globalCurrencyCode
            baseCurrencyCode
            channelCurrencyCode
            cartCurrencyCode
            grandTotal
            baseGrandTotal
            subTotal
            baseSubTotal
            taxTotal
            baseTaxTotal
            discountAmount
            baseDiscountAmount
            checkoutMethod
            isGuest
            isActive
            conversionTime
            customerId
            channelId
            appliedCartRuleIds
            createdAt
            updatedAt 
        }
    }
}

